          </Card>
          <br />
          <ButtonGroup>
            <Button>Save Draft</Button>
            <Button primary>Save & Review</Button>
          </ButtonGroup>
        </Layout.Section>
      </Layout>
      {showemoji && (
        <div className="polaris-emoji-mart">
          <FormLayout>
            <Picker />
          </FormLayout>
        </div>
      )}
      <div style={{ paddingBottom: "100px" }}></div>
    </Page>
